import React, { useState, useEffect } from "react";
import axios from "axios";

function ReportButton() {
  const [key, setKey] = useState([]);
  const [value, setValue] = useState([]);
  const [minPrice, setMinPrice] = useState("");
  const [maxPrice, setMaxPrice] = useState("");
  const [filteredValue, setFilteredValue] = useState([]);

  const fetchData = async () => {
    try {
      const response = await axios.get("http://localhost:3000/report-page");

      setKey(Object.keys(response.data[0])); //FOR COLON

      const valueArray = []; //TODO OK VALULERİ EKLYOR ARRAY'E

      for (let i = 0; i < Object.values(response.data).length; i++) {
        valueArray.push(Object.values(response.data[i]));
      }
      setValue(valueArray); //FOR ROW
      setFilteredValue(valueArray); // Initialize filtered value with all data
    } catch (error) {
      console.error("Error fetching data", error);
    }
  };

  useEffect(() => {
    filterDataByPrice();
  }, [minPrice, maxPrice, value]);

  const filterDataByPrice = () => {
    if (!minPrice && !maxPrice) {
      setFilteredValue(value);
      return;
    }

    const filteredData = value.filter((row) => {
      const price = parseFloat(row[key.indexOf("price")]); // Assuming "price" is one of the keys
      if (isNaN(price)) return false;

      if (minPrice && maxPrice) {
        return price >= parseFloat(minPrice) && price <= parseFloat(maxPrice);
      } else if (minPrice) {
        return price >= parseFloat(minPrice);
      } else {
        return price <= parseFloat(maxPrice);
      }
    });

    setFilteredValue(filteredData);
  };

  return (
    <div>
      <div className="flex">
        <button
          onClick={fetchData}
          className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded mr-2"
        >
          Veriyi Getir
        </button>
        <input
          type="text"
          value={minPrice}
          onChange={(e) => setMinPrice(e.target.value)}
          placeholder="Min Fiyat"
          className="border-2 border-gray-300 p-2 mr-2"
        />
        <input
          type="text"
          value={maxPrice}
          onChange={(e) => setMaxPrice(e.target.value)}
          placeholder="Max Fiyat"
          className="border-2 border-gray-300 p-2 mr-2"
        />
      </div>
      {key.length > 0 && filteredValue.length > 0 && (
        <table className="table-auto mt-4">
          <thead>
            <tr>
              {key.map((item, index) => (
                <th key={index} className="px-4 py-2 bg-gray-200 text-gray-800">
                  {item}
                </th>
              ))}
            </tr>
          </thead>
          <tbody>
            {filteredValue.map((innerArray, outerIndex) => (
              <tr key={outerIndex}>
                {innerArray.map((item, innerIndex) => (
                  <td key={innerIndex} className="border px-4 py-2">
                    {item}
                  </td>
                ))}
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default ReportButton;

