import React, { useState, useEffect } from "react";
import axios from "axios";

function ReportButton() {
  const [key, setKey] = useState([]);
  const [value, setValue] = useState([]);
  const [filterKey, setFilterKey] = useState("");
  const [filteredData, setFilteredData] = useState([]);

  const fetchData = async () => {
    try {
      const response = await axios.get("http://localhost:3000/report-page");

      setKey(Object.keys(response.data[0])); //FOR COLON

      const valueArray = []; //TODO OK VALULERİ EKLYOR ARRAY'E

      for (let i = 0; i < Object.values(response.data).length; i++) {
        valueArray.push(Object.values(response.data[i]));
      }
      setValue(valueArray); //FOR ROW
    } catch (error) {
      console.error("Error fetching data", error);
    }
  };

  const handleFilterKeyChange = (event) => {
    setFilterKey(event.target.value);
  };

  const applyFilter = () => {
    if (!filterKey) {
      // Filtre anahtarı boşsa, filtrelenmiş veri olarak tüm veriyi göster
      setFilteredData(value);
    } else {
      // Filtre anahtarı doluysa, veri setinde dolaşarak filtreleme yap
      const filtered = value.filter((row) => row.hasOwnProperty(filterKey));
      setFilteredData(filtered);
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  return (
    <div>
      <button
        onClick={fetchData}
        className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded mr-2"
      >
        Veriyi Getir
      </button>
      <input
        type="text"
        placeholder="Filtreleme anahtarı girin"
        value={filterKey}
        onChange={handleFilterKeyChange}
        className="mr-2"
      />
      <button
        onClick={applyFilter}
        className="bg-green-500 hover:bg-green-700 text-white font-bold py-2 px-4 rounded"
      >
        Filtrele
      </button>
      {key.length > 0 &&
        (filteredData.length > 0 ? filteredData : value).length > 0 && (
          <table className="table-auto mt-4">
            <thead>
              <tr>
                {key.map((item, index) => (
                  <th
                    key={index}
                    className="px-4 py-2 bg-gray-200 text-gray-800"
                  >
                    {item}
                  </th>
                ))}
              </tr>
            </thead>
            <tbody>
              {(filteredData.length > 0 ? filteredData : value).map(
                (innerArray, outerIndex) => (
                  <tr key={outerIndex}>
                    {innerArray.map((item, innerIndex) => (
                      <td key={innerIndex} className="border px-4 py-2">
                        {item}
                      </td>
                    ))}
                  </tr>
                )
              )}
            </tbody>
          </table>
        )}
    </div>
  );
}

export default ReportButton;

