import React, { createContext, useContext, useState, useEffect } from "react";

const UseContext = createContext();

export const UseProvider = ({ children }) => {
  const [selectedRecNo, setSelectedRecNo] = useState("");

  const [selectedQuery, setSelectedQuery] = useState(localStorage.getItem(""));
  const [selectedReportTitle, setSelectedReportTitle] = useState(
    localStorage.getItem("")
  );

  useEffect(() => {
    const localStorageKey = `report_${selectedRecNo}`;
    const storedSelectedQuery = localStorage.getItem(
      `${localStorageKey}_selectedQuery`
    );
    const storedSelectedReportTitle = localStorage.getItem(
      `${localStorageKey}_selectedReportTitle`
    );
    const storedSelectedRecNo = localStorage.getItem(
      `${localStorageKey}_selectedRecNo`
    );
    localStorage.setItem(storedSelectedQuery, selectedQuery);
    localStorage.setItem(storedSelectedReportTitle, selectedReportTitle);
    localStorage.setItem(storedSelectedRecNo, selectedRecNo);

    console.log(localStorageKey, storedSelectedQuery);
  });

  return (
    <UseContext.Provider
      value={{
        selectedQuery,
        setSelectedQuery,
        selectedReportTitle,
        setSelectedReportTitle,
        selectedRecNo,
        setSelectedRecNo,
        //selectedInitRun,
        //setSelectedInitRun,
      }}
    >
      {children}
    </UseContext.Provider>
  );
};

export const useQuery = () => useContext(UseContext);

