import React, { useState, useEffect } from "react";
import axios from "axios";

function ReportButton() {
  const [key, setKey] = useState([]);
  const [value, setValue] = useState([]);
  const [filteredValue, setFilteredValue] = useState([]); // Filtrelenmiş veriler

  const fetchData = async () => {
    try {
      const response = await axios.get("http://localhost:3000/report");

      setKey(Object.keys(response.data[0])); //FOR COLON

      const valueArray = response.data.map((obj) => Object.values(obj));
      setValue(valueArray); //FOR ROW
    } catch (error) {
      console.error("Error fetching data", error);
    }
  };

  const handleFilter = () => {
    // Burada filtreleme işlemini gerçekleştir
    // Örneğin, key[0] (ilk sütun) üzerinden basit bir filtre uygulayalım
    const filteredData = value.filter((item) => {
      // Filtreleme kriterlerine göre ayarlayabilirsiniz
      // Şu anda, ilk sütun (key[0]) üzerinden filtreleme yapıyoruz
      return item[0] === "filtre_değeri"; // filtre_değeri, filtrede kullanılacak gerçek bir değer olmalıdır
    });
    setFilteredValue(filteredData);
  };

  return (
    <div>
      <button
        onClick={fetchData}
        className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded"
      >
        Veriyi Getir
      </button>
      <button
        onClick={handleFilter}
        className="bg-green-500 hover:bg-green-700 text-white font-bold py-2 px-4 rounded ml-4"
      >
        Filtrele
      </button>
      {key.length > 0 && value.length > 0 && (
        <table className="table-auto mt-4">
          <thead>
            <tr>
              {key.map((item, index) => (
                <th key={index} className="px-4 py-2 bg-gray-200 text-gray-800">
                  {item}
                </th>
              ))}
            </tr>
          </thead>
          <tbody>
            {filteredValue.length > 0
              ? filteredValue.map((innerArray, outerIndex) => (
                  <tr key={outerIndex}>
                    {innerArray.map((item, innerIndex) => (
                      <td key={innerIndex} className="border px-4 py-2">
                        {item}
                      </td>
                    ))}
                  </tr>
                ))
              : value.map((innerArray, outerIndex) => (
                  <tr key={outerIndex}>
                    {innerArray.map((item, innerIndex) => (
                      <td key={innerIndex} className="border px-4 py-2">
                        {item}
                      </td>
                    ))}
                  </tr>
                ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default ReportButton;
