import React, { useState, useEffect, createContext, useContext } from "react";
import { Link } from "react-router-dom";
import axios from "axios";

//Bağlamı Oluştur
const TreeViewContext = createContext();

function TreeView() {
  //const [recNo, setRecNo] = useState([]); //TODO REPORT PAGE SAYFASINA AKTARILACAK PARAMETRİK OLARAK
  //const [reportTitle, setReportTitle] = useState([]);
  const [menuName, setMenuName] = useState([]); // TODO REPORT PAGE SAYFASINA AKTARILACAK SAYFANIN BAŞLIĞI  OLARAK
  const [query, setQuery] = useState([]); // TODO REPORT PAGE SAYFASINA AKATARIALACAK SORGU OLARAK  OLARAK
  //const [initRun, setInitRun] = useState([]); // TODO REPORT PAGE SAYFASINA AKATARILACAK DEĞER  OLARAK

  useEffect(() => {
    async function fetchMenuItems() {
      try {
        const response = await axios.get("/tree-view");

        const menuArray = [];
        const queryArray = [];
        for (let i = 0; i < Object.values(response.data).length; i++) {
          menuArray.push(Object.values(response.data[i])[2]);
          queryArray.push(Object.values(response.data[i])[6]);
        }
        setMenuName(menuArray);
        setQuery(queryArray);
        console.log(queryArray);
      } catch (error) {
        console.error("Failed to fetch menu items: ", error);
      }
    }
    fetchMenuItems();
  }, []);

  // Stateleri içeren bağlamı oluştur
  const treeViewContextValue = {
    //recNo,
    //reportTitle,
    query, //TODO BURADAKİ QUERY ÖNEMLİ ÇÜNKÜ HANGİ MENÜYÜ SEÇERSE ONUN QUERY Sİ Nİ DİĞER KOMPONENTE AKTARMAK GEREKİR VE BUNU YAPMAK İÇİN DE TIKLADIĞIMIZ MENU İLE QUERY Yİ EŞLEŞTİRMEK GEREKLİ YANİ TIKLANILAN ÖĞE KENDİ TABLOSUNDAKİ BU SATIRLARI TETİKLEMELİDİR
    //initRun,
  };

  return (
    <div>
      <h1>RAPORLAR</h1>
      <ul>
        {menuName.map((menuItem, index) => (
          <li key={index}>
            <Link to={`/report-page/${menuItem}`}>{menuItem}</Link>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default TreeView;

