import React, { useState, useEffect } from "react";
import axios from "axios";

function ReportButton() {
  const [key, setKey] = useState([]);
  const [value, setValue] = useState([]);
  const [minPrice, setMinPrice] = useState("");
  const [maxPrice, setMaxPrice] = useState("");

  const fetchData = async () => {
    try {
      const response = await axios.get("http://localhost:3000/report");

      setKey(Object.keys(response.data[0])); //FOR COLON

      const valueArray = response.data.map((item) => Object.values(item)); // OK VALULERİ ARRAY'E EKLENİYOR

      setValue(valueArray); //FOR ROW
    } catch (error) {
      console.error("Error fetching data", error);
    }
  };

  const handleFilter = () => {
    if (!minPrice || !maxPrice) return; // Minimum ve maksimum fiyatlar boşsa, işlemi durdur

    const filteredRows = value.filter((row) => {
      const price = parseInt(row[key.indexOf("Fiyati")]); // "Fiyati" sütununun indeksini al ve fiyatı bu indeksle bul
      return price >= parseInt(minPrice) && price <= parseInt(maxPrice);
    });

    setValue(filteredRows);
  };

  return (
    <div>
      <div>
        <input
          type="number"
          placeholder="Minimum Fiyat"
          value={minPrice}
          onChange={(e) => setMinPrice(e.target.value)}
        />
        <input
          type="number"
          placeholder="Maksimum Fiyat"
          value={maxPrice}
          onChange={(e) => setMaxPrice(e.target.value)}
        />
        <button
          onClick={handleFilter}
          className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded ml-2"
        >
          Fiyata Göre Filtrele
        </button>
      </div>
      {key.length > 0 && value.length > 0 && (
        <table className="table-auto mt-4">
          <thead>
            <tr>
              {key.map((item, index) => (
                <th key={index} className="px-4 py-2 bg-gray-200 text-gray-800">
                  {item}
                </th>
              ))}
            </tr>
          </thead>
          <tbody>
            {value.map((innerArray, outerIndex) => (
              <tr key={outerIndex}>
                {innerArray.map((item, innerIndex) => (
                  <td key={innerIndex} className="border px-4 py-2">
                    {item}
                  </td>
                ))}
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default ReportButton;
