import React, { createContext, useContext, useState, useEffect } from "react";

const UseContext = createContext();

export const UseProvider = ({ children }) => {
  const [selectedQuery, setSelectedQuery] = useState(
    localStorage.getItem("selectedQuery") || ""
  );
  const [selectedReportTitle, setSelectedReportTitle] = useState(
    localStorage.getItem("selectedReportTitle") || ""
  );
  const [selectedInitRun, setSelectedInitRun] = useState(
    localStorage.getItem("selectedInitRun") || ""
  );

  useEffect(() => {
    // Yeni bir recNo oluştur
    const newRecNo = Date.now();

    // localStorage'a yeni bir kayıt oluştur
    localStorage.setItem(
      `report_${newRecNo}`,
      JSON.stringify({
        selectedQuery,
        selectedReportTitle,
        selectedInitRun,
      })
    );

    // localStorage'a yeni recNo'yu kaydet
    localStorage.setItem("selectedRecNo", newRecNo);

    // setSelectedRecNo(newRecNo); // Eğer gerekirse setSelectedRecNo ile state'i güncelle
  }, [selectedQuery, selectedReportTitle, selectedInitRun]);

  return (
    <UseContext.Provider
      value={{
        selectedQuery,
        setSelectedQuery,
        selectedReportTitle,
        setSelectedReportTitle,
        selectedInitRun,
        setSelectedInitRun,
      }}
    >
      {children}
    </UseContext.Provider>
  );
};

export const useQuery = () => useContext(UseContext);
