import React, { createContext, useContext, useState, useEffect } from "react";

const UseContext = createContext();

export const UseProvider = ({ children }) => {
  // localStorage'den tüm verileri bir string olarak alıp ayrıştırma fonksiyonu
  const parseLocalStorageData = () => {
    const storedData = localStorage.getItem("myAppData");
    if (storedData) {
      return JSON.parse(storedData);
    }
    return {};
  };

  // Tüm verilerin durumu
  const [data, setData] = useState(parseLocalStorageData());

  // useEffect hook'u kullanarak tüm verileri localStorage'e yazma
  useEffect(() => {
    localStorage.setItem("myAppData", JSON.stringify(data));
  }, [data]);

  return (
    <UseContext.Provider
      value={{
        data,
        setData,
      }}
    >
      {children}
    </UseContext.Provider>
  );
};

export const useQuery = () => useContext(UseContext);
