import "./App.css";
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import HomePage from "./pages/HomePage/HomePage";
import ReportPage from "./pages/ReportPage/ReportPage";
import { UseProvider, useQuery } from "./pages/HomePage/useContext";

function App() {
  const { selectedMenuName } = useQuery();

  return (
    <Router>
      <UseProvider>
        <Routes>
          <Route path="/" element={<HomePage />} />
          {/* Her rapor için özgün bir URL belirle */}
          <Route
            path={`/report-page/${selectedMenuName}`}
            element={<ReportPage />}
          />
        </Routes>
      </UseProvider>
    </Router>
  );
}

export default App;
