// QueryContext.js
import React, { createContext, useContext, useState } from "react";

const UseContext = createContext();

export const QueryProvider = ({ children }) => {
  const [selectedQuery, setSelectedQuery] = useState("");
  const [selectedReportTitle, setSelectedReportTitle] = useState("");
  const [selectedRecNo, setSelectedRecNo] = useState("");
  const [selectedInitRun, setSelectedInitRun] = useState("");

  return (
    <UseContext.Provider
      value={{
        selectedQuery,
        setSelectedQuery,
        selectedReportTitle,
        setSelectedReportTitle,
        selectedRecNo,
        setSelectedRecNo,
        selectedInitRun,
        setSelectedInitRun,
      }}
    >
      {children}
    </UseContext.Provider>
  );
};

export const useQuery = () => useContext(UseContext);

