import React, { useState, useEffect } from "react";
import axios from "axios";

function ReportButton() {
  //const [data, setData] = useState([]);
  const [key, setKey] = useState([]);
  const [value, setValue] = useState([]);

  // BUG bu effect sayfa yenilenince çalışmamalı sadece butona basınca çalışmalı BUG
  const fetchData = useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get("http://localhost:3000/report-page");

        setKey(Object.keys(response.data[0]));

        const valueArray = []; //TODO OK VALULERİ EKLYOR ARRAY'E

        for (let i = 0; i < Object.values(response.data).length; i++) {
          valueArray.push(Object.values(response.data[i]));
        }
        console.log(valueArray);

        setValue(valueArray);
      } catch (error) {
        console.error("Error fetching data", error);
      }
    };
    fetchData();
  }, []);

  // const handleClick = () => {
  //   console.log(key);
  //   //console.log(value);
  // };

  return (
    <div>
      <button onClick={fetchData}>Veriyi Getir</button>
      <table>
        <tbody>
          <tr>
            {key.map((item, index) => (
              <td key={index}>{item}</td>
            ))}
          </tr>
          {value.map((innerArray, outerIndex) => (
            <tr key={outerIndex}>
              {innerArray.map((item, innerIndex) => (
                <td key={innerIndex}>{item}</td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default ReportButton;

