import React, { createContext, useContext, useState, useEffect } from "react";

const UseContext = createContext();

export const UseProvider = ({ children }) => {
  const [selectedQuery, setSelectedQuery] = useState("");
  const [selectedReportTitle, setSelectedReportTitle] = useState("");
  const [selectedRecNo, setSelectedRecNo] = useState("");
  const [selectedInitRun, setSelectedInitRun] = useState("");

  useEffect(() => {
    const localStorageKey = `report_${selectedRecNo}`;
    const storedSelectedQuery = localStorage.setItem(
      `${localStorageKey}_selectedQuery`
    );
    const storedSelectedReportTitle = localStorage.setItem(
      `${localStorageKey}_selectedReportTitle`
    );
    const storedSelectedRecNo = localStorage.setItem(
      `${localStorageKey}_selectedRecNo`
    );
    const storedSelectedInitRun = localStorage.setItem(
      `${localStorageKey}_selectedInitRun`
    );

    if (
      storedSelectedQuery &&
      storedSelectedReportTitle &&
      storedSelectedRecNo &&
      storedSelectedInitRun
    ) {
      setSelectedQuery(storedSelectedQuery);
      setSelectedReportTitle(storedSelectedReportTitle);
      setSelectedRecNo(storedSelectedRecNo);
      setSelectedInitRun(storedSelectedInitRun);
    }
  }, []);

  return (
    <UseContext.Provider
      value={{
        selectedQuery,
        setSelectedQuery,
        selectedReportTitle,
        setSelectedReportTitle,
        selectedRecNo,
        setSelectedRecNo,
        selectedInitRun,
        setSelectedInitRun,
      }}
    >
      {children}
    </UseContext.Provider>
  );
};

export const useQuery = () => useContext(UseContext);
