import React, { useState, useEffect } from "react";
import axios from "axios";
import { useQuery } from "../HomePage/useContext";

function ButtonCriterion() {
  const [criteriaTitle, setCriteriaTitle] = useState([]);
  const [criteriaType, setCriteriaType] = useState([]);
  const [formData, setFormData] = useState({});
  const [key, setKey] = useState([]);
  const [value, setValue] = useState([]);

  const { selectedReportTitle, selectedQuery, selectedRecNo } = useQuery();

  useEffect(() => {
    handleClick();
  }, []); // Boş bağımlılık dizisi ile sadece sayfa yüklendiğinde çalışmasını sağlar

  const handleClick = async () => {
    try {
      const response = await axios.post("/criterion", { selectedRecNo });

      const criteriaArray = [];
      const criteriaTypeArray = [];

      for (let i = 0; i < Object.values(response.data).length; i++) {
        criteriaArray.push(Object.values(response.data[i])[3]);
        criteriaTypeArray.push(Object.values(response.data[i])[4]);
      }
      setCriteriaTitle(criteriaArray);
      setCriteriaType(criteriaTypeArray);
    } catch (error) {
      console.error("Error fetching data", error);
    }
  };

  //BUG BU KISIMDA İŞLEYİŞ BİRAZ YANLIŞ İNPUT ALINDIKTAN SONRA BUTONA TIKLANINCA ÇALIŞMASI GEREK
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("/postQuery", {
        selectedQuery,
        formData,
      });

      // Sunucudan dönen filtrelenmiş veriyi state'e ata
      const columns = Object.keys(response.data[0]);
      setKey(columns); //FOR COLON //TODO BU KISIMLARI AÇIKLAYACAK BİİÇİMDE KOD YAZ

      const valueArray = []; //TODO OK VALULERİ EKLYOR ARRAY'E
      const rows = Object.values(response.data);

      for (let i = 0; i < rows.length; i++) {
        valueArray.push(Object.values(response.data[i]));
      }
      setValue(valueArray); //FOR ROW //TODO BU KISIMLARI AÇIKLAYACAK BİİÇİMDE KOD YAZ
    } catch (error) {
      console.error("Error submitting form", error);
    }
  };

  const handleInputChange = (e, title) => {
    const value = e.target.value;
    const newFormData = { ...formData, [title]: value };
    setFormData(newFormData);
    //handleSubmit();//BUG
  };

  return (
    <div className="p-4">
      <div>
        <h1 className="text-2xl font-bold mb-4">{selectedReportTitle}</h1>
      </div>
      <h1 className="text-2xl font-bold mb-4">Kriterler</h1>

      <form onSubmit={handleSubmit}>
        {criteriaTitle.map((title, index) => (
          <div key={index} className="mb-6">
            <h2 className="text-lg font-semibold mb-2">{title}</h2>

            {criteriaType[index] === "Numeric" ? (
              <div>
                <input
                  type="text"
                  placeholder="Alt Sayı"
                  className="border rounded-md px-3 py-2 mb-2"
                  onChange={(e) => handleInputChange(e, title)}
                />
                <br />
                <input
                  type="text"
                  placeholder="Üst Sayı"
                  className="border rounded-md px-3 py-2"
                  onChange={(e) => handleInputChange(e, title)}
                />
              </div>
            ) : criteriaType[index] === "Text" ? (
              <input
                type="text"
                placeholder=""
                className="border rounded-md px-3 py-2 mb-2"
                onChange={(e) => handleInputChange(e, title)}
              />
            ) : criteriaType[index] === "Date" ? (
              <input
                type="date"
                className="border rounded-md px-3 py-2 mb-2"
                onChange={(e) => handleInputChange(e, title)}
              />
            ) : criteriaType[index] === "Combo" ? (
              <select
                className="border rounded-md px-3 py-2 mb-2"
                onChange={(e) => handleInputChange(e, title)}
              >
                <option>Seçenek 1</option>
                <option>Seçenek 2</option>
                <option>Seçenek 3</option>
              </select>
            ) : null}
          </div>
        ))}
        <button
          type="submit"
          className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded"
        >
          RAPORLA
        </button>
      </form>

      {/* Filtrelenmiş verileri tabloda göster */}
      <div className="mt-4">
        {key.length > 0 && value.length > 0 && (
          <table className="table-auto mt-4">
            <thead>
              <tr>
                {key.map((item, index) => (
                  <th
                    key={index}
                    className="px-4 py-2 bg-gray-200 text-gray-800"
                  >
                    {item}
                  </th>
                ))}
              </tr>
            </thead>
            <tbody>
              {value.map((innerArray, outerIndex) => (
                <tr key={outerIndex}>
                  {innerArray.map((item, innerIndex) => (
                    <td key={innerIndex} className="border px-4 py-2">
                      {item}
                    </td>
                  ))}
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
}

export default ButtonCriterion;
