import React, { useState, useEffect } from "react";
import axios from "axios";

function ReportButton() {
  const [key, setKey] = useState([]);
  const [value, setValue] = useState([]);
  const [filteredValue, setFilteredValue] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const response = await axios.get("http://localhost:3000/report");

      setKey(Object.keys(response.data[0])); //FOR COLON

      const valueArray = response.data.map((item) => Object.values(item)); // OK VALULERİ ARRAY'E EKLENİYOR

      setValue(valueArray); //FOR ROW
      setFilteredValue(valueArray); // Filtrelenmiş değerler de varsayılan olarak tüm değerlerle aynı olmalı
    } catch (error) {
      console.error("Error fetching data", error);
    }
  };

  const handleFilter = () => {
    const filteredRows = value.filter((row) => {
      // Burada filtreleme işlemleri gerçekleştirilecek
      // Örneğin, her bir satırı dolaşarak arama terimine uygun olanları filtreleyebilirsiniz
      // Şu anda sadece satırların ilk elemanına (index 0) bakıyoruz, farklı bir kritere göre filtreleme yapabilirsiniz
      return row[0].toLowerCase().includes(searchTerm.toLowerCase());
    });
    setFilteredValue(filteredRows);
  };

  const handleSearch = (e) => {
    setSearchTerm(e.target.value);
  };

  return (
    <div>
      <div>
        <input
          type="text"
          placeholder="Ara..."
          value={searchTerm}
          onChange={handleSearch}
        />
        <button
          onClick={handleFilter}
          className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded ml-2"
        >
          Filtrele
        </button>
      </div>
      {key.length > 0 && filteredValue.length > 0 && (
        <table className="table-auto mt-4">
          <thead>
            <tr>
              {key.map((item, index) => (
                <th key={index} className="px-4 py-2 bg-gray-200 text-gray-800">
                  {item}
                </th>
              ))}
            </tr>
          </thead>
          <tbody>
            {filteredValue.map((innerArray, outerIndex) => (
              <tr key={outerIndex}>
                {innerArray.map((item, innerIndex) => (
                  <td key={innerIndex} className="border px-4 py-2">
                    {item}
                  </td>
                ))}
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default ReportButton;
