import React, { createContext, useContext, useState, useEffect } from "react";

const UseContext = createContext();

// Rastgele önek oluşturma fonksiyonu
const generatePrefix = () => {
  return Math.random().toString(36).substring(7);
};

export const UseProvider = ({ children }) => {
  // Rastgele önek oluştur
  const prefix = generatePrefix();

  const [selectedQuery, setSelectedQuery] = useState(
    localStorage.getItem(`${prefix}_selectedQuery`)
  );
  const [selectedReportTitle, setSelectedReportTitle] = useState(
    localStorage.getItem(`${prefix}_selectedReportTitle`)
  );
  const [selectedRecNo, setSelectedRecNo] = useState(
    localStorage.getItem(`${prefix}_selectedRecNo`)
  );
  const [selectedInitRun, setSelectedInitRun] = useState(
    localStorage.getItem(`${prefix}_selectedInitRun`)
  );

  useEffect(() => {
    localStorage.setItem(`${prefix}_selectedQuery`, selectedQuery);
    localStorage.setItem(`${prefix}_selectedReportTitle`, selectedReportTitle);
    localStorage.setItem(`${prefix}_selectedRecNo`, selectedRecNo);
    localStorage.setItem(`${prefix}_selectedInitRun`, selectedInitRun);
  }, [selectedQuery, selectedReportTitle, selectedRecNo, selectedInitRun]);

  return (
    <UseContext.Provider
      value={{
        selectedQuery,
        setSelectedQuery,
        selectedReportTitle,
        setSelectedReportTitle,
        selectedRecNo,
        setSelectedRecNo,
        selectedInitRun,
        setSelectedInitRun,
      }}
    >
      {children}
    </UseContext.Provider>
  );
};

export const useQuery = () => useContext(UseContext);

