import React, { createContext, useContext, useState, useEffect } from "react";

const UseContext = createContext();

export const UseProvider = ({ children }) => {
  const [selectedQuery, setSelectedQuery] = useState(
    localStorage.getItem(getStorageKey("selectedQuery"))
  );
  const [selectedReportTitle, setSelectedReportTitle] = useState(
    localStorage.getItem(getStorageKey("selectedReportTitle"))
  );
  const [selectedRecNo, setSelectedRecNo] = useState(
    localStorage.getItem(getStorageKey("selectedRecNo"))
  );
  const [selectedInitRun, setSelectedInitRun] = useState(
    localStorage.getItem(getStorageKey("selectedInitRun"))
  );

  useEffect(() => {
    localStorage.setItem(getStorageKey("selectedQuery"), selectedQuery);
    localStorage.setItem(
      getStorageKey("selectedReportTitle"),
      selectedReportTitle
    );
    localStorage.setItem(getStorageKey("selectedRecNo"), selectedRecNo);
    localStorage.setItem(getStorageKey("selectedInitRun"), selectedInitRun);
  });

  // Anahtar oluşturma fonksiyonu
  function getStorageKey(key) {
    return `your_prefix_${key}`;
  }

  return (
    <UseContext.Provider
      value={{
        selectedQuery,
        setSelectedQuery,
        selectedReportTitle,
        setSelectedReportTitle,
        selectedRecNo,
        setSelectedRecNo,
        selectedInitRun,
        setSelectedInitRun,
      }}
    >
      {children}
    </UseContext.Provider>
  );
};

export const useQuery = () => useContext(UseContext);

