import React, { createContext, useContext, useState, useEffect } from "react";

const UseContext = createContext();

// Rastgele önek oluşturma fonksiyonu
const useRandomPrefix = () => {
  const randomString = Math.random().toString(36).substring(7);
  return `_${randomString}`;
};

export const UseProvider = ({ children }) => {
  // Rastgele önekler oluştur
  const prefix = useRandomPrefix();

  // Anahtarlar
  const keys = {
    selectedQuery: `${prefix}_selectedQuery`,
    selectedReportTitle: `${prefix}_selectedReportTitle`,
    selectedRecNo: `${prefix}_selectedRecNo`,
    selectedInitRun: `${prefix}_selectedInitRun`,
  };

  // Anahtarların değerlerini localStorage'dan al ve state'lere ata
  const [selectedQuery, setSelectedQuery] = useState(
    localStorage.getItem(keys.selectedQuery)
  );
  const [selectedReportTitle, setSelectedReportTitle] = useState(
    localStorage.getItem(keys.selectedReportTitle)
  );
  const [selectedRecNo, setSelectedRecNo] = useState(
    localStorage.getItem(keys.selectedRecNo)
  );
  const [selectedInitRun, setSelectedInitRun] = useState(
    localStorage.getItem(keys.selectedInitRun)
  );

  useEffect(() => {
    // Değişen state değerlerini localStorage'e kaydet
    localStorage.setItem(keys.selectedQuery, selectedQuery);
    localStorage.setItem(keys.selectedReportTitle, selectedReportTitle);
    localStorage.setItem(keys.selectedRecNo, selectedRecNo);
    localStorage.setItem(keys.selectedInitRun, selectedInitRun);
  }, [selectedQuery, selectedReportTitle, selectedRecNo, selectedInitRun]);

  return (
    <UseContext.Provider
      value={{
        selectedQuery,
        setSelectedQuery,
        selectedReportTitle,
        setSelectedReportTitle,
        selectedRecNo,
        setSelectedRecNo,
        selectedInitRun,
        setSelectedInitRun,
      }}
    >
      {children}
    </UseContext.Provider>
  );
};

export const useQuery = () => useContext(UseContext);

