import React, { useState, useEffect } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";

function ButtonCriterion() {
  const [criteriaTitle, setCriteriaTitle] = useState([]);
  const [criteriaType, setCriteriaType] = useState([]);
  const [comboOptions, setComboOptions] = useState([]);
  const [defaultDeger, setDefaultDeger] = useState([]);

  const [formData, setFormData] = useState({});
  const [key, setKey] = useState([]);
  const [value, setValue] = useState([]);
  //const [formError, setFormError] = useState(false); // Hata durumunu takip etmek için

  const { recNo, queryItem, reportTitle } = useParams();

  useEffect(() => {
    handleClick();
  }, []);

  const handleClick = async () => {
    try {
      const response = await axios.post("/criterion", { recNo });

      const criteriaTitleArray = [];
      const criteriaTypeArray = [];
      const optionsArray = [];
      let defaultDegerArray = [];

      let criteriaQuery = ""; //TODO ARRAY OLMASI LAZIM
      //TODO DEFAULT DEĞERİ ARRAYİ OLUŞTUR ONU DA ARKAYA YOLLA

      for (let i = 0; i < Object.values(response.data).length; i++) {
        criteriaTitleArray.push(Object.values(response.data[i])[3]); //CRITERIA TITLE
        criteriaTypeArray.push(Object.values(response.data[i])[4]); //CRITERIA TYPE
        criteriaQuery = Object.values(response.data[i])[5]; //CRITERIA QUERY
        defaultDegerArray = Object.values(response.data[i])[6]; //DEFAULT DEĞERİ
      }
      setCriteriaTitle(criteriaTitleArray);
      setCriteriaType(criteriaTypeArray);
      setDefaultDeger(defaultDegerArray);

      const response2 = await axios.post("/combo-options", {
        criteriaQuery,
        //defaultDeger,
      });

      for (let i = 0; i < Object.values(response2.data).length; i++) {
        optionsArray.push(Object.values(response2.data[i]));
      }

      setComboOptions(optionsArray);
    } catch (error) {
      console.error("Error fetching data", error);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      //   let hasError = false;

      //   for (const title of criteriaTitle) {
      //     if (!formData[title]) {
      //       hasError = true;
      //       break;
      //     }
      //   }

      // if (hasError) {
      //   setFormError(true);
      //   return;
      // }

      // setFormError(false);

      const response = await axios.post("/postQuery", {
        queryItem,
        formData,
        defaultDeger,
      });

      const columns = Object.keys(response.data[0]);
      setKey(columns);

      const valueArray = [];
      const rows = Object.values(response.data);

      for (let i = 0; i < rows.length; i++) {
        valueArray.push(Object.values(response.data[i]));
      }
      setValue(valueArray);
    } catch (error) {
      console.error("Error submitting form", error);
    }
  };

  const handleInputChange = (e, title) => {
    const value = e.target.value;
    const newFormData = { ...formData, [title]: value };
    setFormData(newFormData);
  };

  return (
    <div className="p-4">
      <div>
        <h1 className="text-2xl font-bold mb-4">{reportTitle}</h1>
      </div>

      <form onSubmit={handleSubmit}>
        {criteriaTitle.map((title, index) => (
          <div key={index} className="mb-6">
            <h2 className="text-lg font-semibold mb-2">{title}</h2>

            {criteriaType[index] === "Numeric" ? (
              <div>
                <input
                  type="text"
                  placeholder={`${title}`}
                  // className={`border rounded-md px-3 py-2 mb-2 ${
                  //   (formError && !formData[title]) ||
                  //   (!formError && formData[title] === "") // Boş input kontrolü
                  //     ? "border-red-500"
                  //     : ""
                  // }`}
                  onChange={(e) => handleInputChange(e, title)}
                />
              </div>
            ) : criteriaType[index] === "Text" ? (
              <input
                type="text"
                placeholder={`${title}`}
                // className={`border rounded-md px-3 py-2 mb-2 ${
                //   (formError && !formData[title]) ||
                //   (!formError && formData[title] === "") // Boş input kontrolü
                //     ? "border-red-500"
                //     : ""
                // }`}
                onChange={(e) => handleInputChange(e, title)}
              />
            ) : criteriaType[index] === "Date" ? (
              <input
                type="date"
                // className={`border rounded-md px-3 py-2 mb-2 ${
                //   (formError && !formData[title]) ||
                //   (!formError && formData[title] === "") // Boş input kontrolü
                //     ? "border-red-500"
                //     : ""
                // }`}
                onChange={(e) => handleInputChange(e, title)}
              />
            ) : criteriaType[index] === "Combo" ? (
              <select
                // className={`border rounded-md px-3 py-2 mb-2 ${
                //   (formError && !formData[title]) ||
                //   (!formError && formData[title] === "") // Boş input kontrolü
                //     ? "border-red-500"
                //     : ""
                // }`}
                onChange={(e) => handleInputChange(e, title)}
              >
                <option>Seçiniz</option>

                <option>Hepsi</option>
                {comboOptions.map((option, index) => (
                  <option key={index}>{option}</option>
                ))}
              </select>
            ) : null}
          </div>
        ))}
        <button
          type="submit"
          className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded"
        >
          RAPORLA
        </button>
        {/* {formError && (
          <p className="text-red-500 mt-2">Lütfen tüm değerleri giriniz.</p>
        )} */}
      </form>

      <div className="mt-4">
        {key.length > 0 && value.length > 0 && (
          <table className="table-auto mt-4">
            <thead>
              <tr>
                {key.map((item, index) => (
                  <th
                    key={index}
                    className="px-4 py-2 bg-gray-200 text-gray-800"
                  >
                    {item}
                  </th>
                ))}
              </tr>
            </thead>
            <tbody>
              {value.map((innerArray, outerIndex) => (
                <tr key={outerIndex}>
                  {innerArray.map((item, innerIndex) => (
                    <td key={innerIndex} className="border px-4 py-2">
                      {item}
                    </td>
                  ))}
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
}

export default ButtonCriterion;
