import React, { useState, useEffect } from "react";
import axios from "axios";

function ReportButton() {
  const [key, setKey] = useState([]);
  const [value, setValue] = useState([]);
  const [minPrice, setMinPrice] = useState("");
  const [maxPrice, setMaxPrice] = useState("");

  const fetchData = async () => {
    try {
      const response = await axios.get("http://localhost:3000/report-page");

      setKey(Object.keys(response.data[0])); //FOR COLON

      const valueArray = []; //TODO OK VALULERİ EKLYOR ARRAY'E

      for (let i = 0; i < Object.values(response.data).length; i++) {
        valueArray.push(Object.values(response.data[i]));
      }
      setValue(valueArray); //FOR ROW
    } catch (error) {
      console.error("Error fetching data", error);
    }
  };

  const handleMinPriceChange = (event) => {
    setMinPrice(event.target.value);
  };

  const handleMaxPriceChange = (event) => {
    setMaxPrice(event.target.value);
  };

  const applyFilter = () => {
    // Filtreleme işlemlerini burada gerçekleştirin
    // Örneğin, minPrice ve maxPrice'e göre filtreleme yapabilirsiniz
    // Filtreleme işlemleri tamamlandıktan sonra setState kullanarak value state'ini güncelleyin
  };

  return (
    <div>
      <button
        onClick={fetchData}
        className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded"
      >
        Veriyi Getir
      </button>
      {key.length > 0 && value.length > 0 && (
        <div className="mt-4">
          <label htmlFor="minPrice" className="mr-2">
            Min Fiyat:
          </label>
          <input
            type="number"
            id="minPrice"
            value={minPrice}
            onChange={handleMinPriceChange}
            className="mr-2"
          />
          <label htmlFor="maxPrice" className="mr-2">
            Max Fiyat:
          </label>
          <input
            type="number"
            id="maxPrice"
            value={maxPrice}
            onChange={handleMaxPriceChange}
            className="mr-2"
          />
          <button
            onClick={applyFilter}
            className="bg-green-500 hover:bg-green-700 text-white font-bold py-2 px-4 rounded"
          >
            Filtrele
          </button>
          <table className="table-auto mt-4">
            <thead>
              <tr>
                {key.map((item, index) => (
                  <th
                    key={index}
                    className="px-4 py-2 bg-gray-200 text-gray-800"
                  >
                    {item}
                  </th>
                ))}
              </tr>
            </thead>
            <tbody>
              {value.map((innerArray, outerIndex) => (
                <tr key={outerIndex}>
                  {innerArray.map((item, innerIndex) => (
                    <td key={innerIndex} className="border px-4 py-2">
                      {item}
                    </td>
                  ))}
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
}

export default ReportButton;

