import React, { useState, useEffect } from "react";
import axios from "axios";

function ReportButton() {
  const [key, setKey] = useState([]);
  const [value, setValue] = useState([]);
  const [criteriaTitle, setCriteriaTitle] = useState([]); //TODO Krtierin başlığını al ve gir input alanının yanına
  const [criteriaType, setCriteriaType] = useState([]); //TODO Kriterin Tipine göre gelecek input alanı

  const [filter, setFilter] = useState([]);

  //TODO CRİTERİALTİTLE'LARIN YER ALDIĞI ARRAY'E BAK EĞER ORADA HERHANGİ BİR KEY BULURSAN O FİLTRELENCEK DEMEKTİR

  const fetchData = async () => {
    try {
      const response = await axios.get("http://localhost:3000/report");

      setKey(Object.keys(response.data[0])); //FOR COLON

      const valueArray = []; //TODO OK VALULERİ EKLYOR ARRAY'E

      for (let i = 0; i < Object.values(response.data).length; i++) {
        valueArray.push(Object.values(response.data[i]));
      }
      setValue(valueArray); //FOR ROW
    } catch (error) {
      console.error("Error fetching data", error);
    }
  };

  const fetchCriteria = async () => {
    try {
      const response = await axios.get("http://localhost:3000/criterion");

      const criteriaArray = [];
      for (let i = 0; i < Object.values(response.data).length; i++) {
        criteriaArray.push(Object.values(response.data[i])[3]); //TODOSdece Başlık depolanır
      }
      setCriteriaTitle(criteriaArray);

      const criteriaTypeArray = [];
      for (let i = 0; i < Object.values(response.data).length; i++) {
        criteriaTypeArray.push(Object.values(response.data[i])[4]); //TODO Kriter tipi
      }

      setCriteriaType(criteriaTypeArray);
    } catch (error) {
      console.error("Error fetching data", error);
    }
  };

  return (
    <div>
      <button
        onClick={fetchData}
        className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded"
      >
        Veriyi Getir
      </button>
      {key.length > 0 && value.length > 0 && (
        <table className="table-auto mt-4">
          <thead>
            <tr>
              {key.map((item, index) => (
                <th key={index} className="px-4 py-2 bg-gray-200 text-gray-800">
                  {item}
                </th>
              ))}
            </tr>
          </thead>
          <tbody>
            {value.map((innerArray, outerIndex) => (
              <tr key={outerIndex}>
                {innerArray.map((item, innerIndex) => (
                  <td key={innerIndex} className="border px-4 py-2">
                    {item}
                  </td>
                ))}
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default ReportButton;

