import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import { useQuery } from "./useContext";

function TreeView() {
  const [menuItems, setMenuItems] = useState([]);

  const [reportTitle, setReportTitle] = useState([]);
  const [menuName, setMenuName] = useState([]);
  const [query, setQuery] = useState([]);

  useEffect(() => {
    async function fetchMenuItems() {
      try {
        const response = await axios.get("/tree-view");

        const menuItemsArray = response.data.map((item) => {
          return {
            menuName: item[2],
            reportTitle: item[5],
            query: item[6],
          };
        });

        setMenuItems(menuItemsArray);
      } catch (error) {
        console.error("Failed to fetch menu items: ", error);
      }
    }
    fetchMenuItems();
  }, []);

  return (
    <div className="p-4 border rounded-lg shadow-md">
      <h1 className="text-2xl font-bold mb-4">RAPORLAR</h1>
      <ul>
        {menuItems.map((menuItem, index) => (
          <li key={index} className="mb-2">
            <Link
              to={`/report-page/${index + 1}/${menuItem.query}`}
              className="block px-4 py-2 w-40 bg-blue-500 text-white rounded-md hover:bg-blue-600"
              target="_blank"
            >
              {menuItem.menuName}
            </Link>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default TreeView;
