import React, { useState, useEffect } from "react";
import axios from "axios";

function ReportButton() {
  const [key, setKey] = useState([]);
  const [value, setValue] = useState([]);
  const [criteriaTitle, setCriteriaTitle] = useState([]); // Kriter başlıkları
  const [criteriaType, setCriteriaType] = useState([]); // Kriter tipleri
  const [filter, setFilter] = useState([]); // Filtre değerleri

  const fetchData = async () => {
    try {
      // Rapor verilerini getir
      const reportResponse = await axios.get("http://localhost:3000/report");
      setKey(Object.keys(reportResponse.data[0]));
      setValue(reportResponse.data.map((obj) => Object.values(obj)));

      // Kriter verilerini getir
      const criteriaResponse = await axios.get(
        "http://localhost:3000/criterion"
      );
      const criteriaArray = criteriaResponse.data.map(
        (obj) => Object.values(obj)[3]
      );
      const criteriaTypeArray = criteriaResponse.data.map(
        (obj) => Object.values(obj)[4]
      );
      setCriteriaTitle(criteriaArray);
      setCriteriaType(criteriaTypeArray);
    } catch (error) {
      console.error("Error fetching data", error);
    }
  };
  é;

  const handleFilter = () => {
    // Filtreleme işlemini burada gerçekleştir
    // Örneğin, burada bir filtreleme fonksiyonu çağırabilirsiniz
  };

  return (
    <div>
      <button
        onClick={fetchData}
        className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded"
      >
        Veriyi Getir
      </button>
      <button
        onClick={handleFilter}
        className="bg-green-500 hover:bg-green-700 text-white font-bold py-2 px-4 rounded ml-4"
      >
        Filtrele
      </button>
      {key.length > 0 && value.length > 0 && (
        <table className="table-auto mt-4">
          <thead>
            <tr>
              {key.map((item, index) => (
                <th key={index} className="px-4 py-2 bg-gray-200 text-gray-800">
                  {item}
                </th>
              ))}
            </tr>
          </thead>
          <tbody>
            {value.map((innerArray, outerIndex) => (
              <tr key={outerIndex}>
                {innerArray.map((item, innerIndex) => (
                  <td key={innerIndex} className="border px-4 py-2">
                    {item}
                  </td>
                ))}
              </tr>
            ))}
          </tbody>
        </table>
      )}

      <div className="p-4">
        <h1 className="text-2xl font-bold mb-4">Kriterler</h1>
        <div>
          {criteriaTitle.map((title, index) => (
            <div key={index} className="mb-6">
              <h2 className="text-lg font-semibold mb-2">
                Kriter Başlığı: {title}
              </h2>

              {criteriaType[index] === "Numeric" ? (
                <div>
                  <input
                    type="text"
                    placeholder="Alt Sayı"
                    className="border rounded-md px-3 py-2 mb-2"
                  />
                  <br />
                  <input
                    type="text"
                    placeholder="Sayı Üst Sayı"
                    className="border rounded-md px-3 py-2"
                  />
                </div>
              ) : criteriaType[index] === "Text" ? (
                <input
                  type="text"
                  placeholder="Metin giriniz"
                  className="border rounded-md px-3 py-2 mb-2"
                />
              ) : criteriaType[index] === "Date" ? (
                <input
                  type="date"
                  className="border rounded-md px-3 py-2 mb-2"
                />
              ) : criteriaType[index] === "Combo" ? (
                <select className="border rounded-md px-3 py-2 mb-2">
                  <option>Seçenek 1</option>
                  <option>Seçenek 2</option>
                  <option>Seçenek 3</option>
                </select>
              ) : null}
            </div>
          ))}
        </div>

        <button className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded">
          RAPORLA
        </button>
      </div>
    </div>
  );
}

export default ReportButton;
