import React, { useState, useEffect } from "react";
import axios from "axios";

function ReportButton() {
  const [key, setKey] = useState([]);
  const [value, setValue] = useState([]);

  const fetchData = async () => {
    try {
      const response = await axios.get("http://localhost:3000/report");

      setKey(Object.keys(response.data[0])); //FOR COLON
      console.log(keyé);

      const valueArray = []; //TODO OK VALULERİ EKLYOR ARRAY'E

      for (let i = 0; i < Object.values(response.data).length; i++) {
        valueArray.push(Object.values(response.data[i]));
      }
      setValue(valueArray); //FOR ROW
      console.log(value);
    } catch (error) {
      console.error("Error fetching data", error);
    }
  };

  return (
    <div>
      <button
        onClick={fetchData}
        className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded"
      >
        Veriyi Getir
      </button>
      {key.length > 0 && value.length > 0 && (
        <table className="table-auto mt-4">
          <thead>
            <tr>
              {key.map((item, index) => (
                <th key={index} className="px-4 py-2 bg-gray-200 text-gray-800">
                  {item}
                </th>
              ))}
            </tr>
          </thead>
          <tbody>
            {value.map((innerArray, outerIndex) => (
              <tr key={outerIndex}>
                {innerArray.map((item, innerIndex) => (
                  <td key={innerIndex} className="border px-4 py-2">
                    {item}
                  </td>
                ))}
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default ReportButton;

