import React, { useState, useEffect } from "react";
import axios from "axios";

function ReportButton() {
  const [key, setKey] = useState([]);
  const [value, setValue] = useState([]);
  const [criteriaTitle, setCriteriaTitle] = useState([]);
  const [criteriaType, setCriteriaType] = useState([]);
  const [filteredValue, setFilteredValue] = useState([]); // Filtrelenmiş veri

  const fetchData = async () => {
    try {
      const response = await axios.get("http://localhost:3000/report");

      setKey(Object.keys(response.data[0]));
      const valueArray = [];
      for (let i = 0; i < Object.values(response.data).length; i++) {
        valueArray.push(Object.values(response.data[i]));
      }
      setValue(valueArray);
    } catch (error) {
      console.error("Error fetching data", error);
    }
  };

  const fetchCriteria = async () => {
    try {
      const response = await axios.get("http://localhost:3000/criterion");

      const criteriaArray = [];
      for (let i = 0; i < Object.values(response.data).length; i++) {
        criteriaArray.push(Object.values(response.data[i])[3]);
      }
      setCriteriaTitle(criteriaArray);

      const criteriaTypeArray = [];
      for (let i = 0; i < Object.values(response.data).length; i++) {
        criteriaTypeArray.push(Object.values(response.data[i])[4]);
      }
      setCriteriaType(criteriaTypeArray);
    } catch (error) {
      console.error("Error fetching data", error);
    }
  };

  const filterData = () => {
    // Filtrelenmiş veriyi oluştur
    const filteredData = value.filter((item) => {
      // Tüm kriterlere göre filtrele
      return criteriaTitle.every((title, index) => {
        const inputValue = document.getElementById(`input_${index}`).value;
        if (!inputValue) return true; // Filtreleme yapılacak bir değer yoksa geç
        // Veriyi kriterlere göre kontrol et
        return item[index] === inputValue;
      });
    });
    // Filtrelenmiş veriyi ayarla
    setFilteredValue(filteredData);
  };

  useEffect(() => {
    fetchData();
    fetchCriteria();
  }, []); // Sadece bileşen yüklendiğinde bir kere veriyi almak için

  return (
    <div>
      <button
        onClick={filterData}
        className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded"
      >
        Raporla
      </button>
      {key.length > 0 && value.length > 0 && (
        <table className="table-auto mt-4">
          <thead>
            <tr>
              {key.map((item, index) => (
                <th key={index} className="px-4 py-2 bg-gray-200 text-gray-800">
                  {item}
                </th>
              ))}
            </tr>
          </thead>
          <tbody>
            {filteredValue.map((innerArray, outerIndex) => (
              <tr key={outerIndex}>
                {innerArray.map((item, innerIndex) => (
                  <td key={innerIndex} className="border px-4 py-2">
                    {item}
                  </td>
                ))}
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default ReportButton;
