import React, { useState, useEffect } from "react";
import axios from "axios";

function ReportButton() {
  //const [data, setData] = useState([]);
  const [key, setKey] = useState([]);
  const [value, setValue] = useState([]);

  // BUG bu effect sayfa yenilenince çalışmamalı sadece butona basınca çalışmalı BUG
  const fetchData = useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get("http://localhost:3000/report-page");

        const key = Object.keys(response.data[0]);
        console.log(key); //TODO OK

        const value = Object.values(response.data);
        console.log(value); //TODO BİR ARRAY'E AKTARMAM GEREK

        const valueArray = [];
        console.log(Object.values(response.data).length);
        for (let i = 0; i < Object.values(response.data).length; i++) {
          valueArray.push(Object.values(response.data[i]));
        }

        console.log(valueArray);

        //setKey(key);
        //setValue(Object.entries(response.data)); // BUG İLK  OBJE GELİYOR SADECE //
        //const entries = Object.values(response.data);
        //console.log(entries[0]);
      } catch (error) {
        console.error("Error fetching data", error);
      }
    };
    fetchData();
  }, [value]);

  // const handleClick = () => {
  //   console.log(key);
  //   //console.log(value);
  // };

  return (
    <div>
      <button onClick={fetchData}>Veriyi Getir</button>
      <table>
        <tbody>
          <tr>
            {key.map((item, index) => (
              <td key={index}>{item}</td>
            ))}
          </tr>
        </tbody>
      </table>
    </div>
  );
}

export default ReportButton;

