import React, { createContext, useContext, useState, useEffect } from "react";

const UseContext = createContext();

export const UseProvider = ({ children }) => {
  const [selectedQuery, setSelectedQueryState] = useState("");
  const [selectedReportTitle, setSelectedReportTitleState] = useState("");
  const [selectedRecNo, setSelectedRecNoState] = useState("");
  const [selectedInitRun, setSelectedInitRunState] = useState("");

  // Tarayıcı yerel depolama kullanarak değerleri saklama ve algoritma
  useEffect(() => {
    // Tarayıcı yerel depolamadan değerleri al
    const selectedQueryFromLocalStorage = localStorage.getItem("selectedQuery");
    const selectedReportTitleFromLocalStorage = localStorage.getItem(
      "selectedReportTitle"
    );
    const selectedRecNoFromLocalStorage = localStorage.getItem("selectedRecNo");
    const selectedInitRunFromLocalStorage =
      localStorage.getItem("selectedInitRun");

    // Alınan değerleri bağlamdaki durum değerlerine ata
    if (selectedQueryFromLocalStorage)
      setSelectedQueryState(selectedQueryFromLocalStorage);
    if (selectedReportTitleFromLocalStorage)
      setSelectedReportTitleState(selectedReportTitleFromLocalStorage);
    if (selectedRecNoFromLocalStorage)
      setSelectedRecNoState(selectedRecNoFromLocalStorage);
    if (selectedInitRunFromLocalStorage)
      setSelectedInitRunState(selectedInitRunFromLocalStorage);
  }, []); // Boş bağımlılık dizisiyle sadece bir kez çalışmasını sağlar

  // Durum değiştiğinde yerel depolamaya değerleri kaydet
  useEffect(() => {
    localStorage.setItem("selectedQuery", selectedQuery);
    localStorage.setItem("selectedReportTitle", selectedReportTitle);
    localStorage.setItem("selectedRecNo", selectedRecNo);
    localStorage.setItem("selectedInitRun", selectedInitRun);
  }, [selectedQuery, selectedReportTitle, selectedRecNo, selectedInitRun]);

  // Bağlam değerlerini döndüren bir nesne
  const contextValue = {
    selectedQuery,
    setSelectedQuery: setSelectedQueryState,
    selectedReportTitle,
    setSelectedReportTitle: setSelectedReportTitleState,
    selectedRecNo,
    setSelectedRecNo: setSelectedRecNoState,
    selectedInitRun,
    setSelectedInitRun: setSelectedInitRunState,
  };

  return (
    <UseContext.Provider value={contextValue}>{children}</UseContext.Provider>
  );
};

export const useQuery = () => useContext(UseContext);
