import React, { createContext, useContext, useState, useEffect } from "react";

const UseContext = createContext();

export const UseProvider = ({ children }) => {
  const [selectedQuery, setSelectedQuery] = useState();
  const [selectedReportTitle, setSelectedReportTitle] = useState();
  const [selectedRecNo, setSelectedRecNo] = useState();
  //const [selectedInitRun, setSelectedInitRun] = useState();
  // localStorage.getItem(selectedInitRun);
  localStorage.getItem("selectedQuery");
  localStorage.getItem("selectedReportTitle");
  localStorage.getItem("selectedRecNo");

  //TODO BURAYA PARAMETRE VE YA BİR DEĞER YOLLUCAZ O PARAMETREDEKİ DEĞER NE İSE ONU ALMASINI İSTİCEZ REFRESHLERKEN VE DİĞERLERİNİ ALMICAK BU SAYEDE İLK

  useEffect(() => {
    // Bu etkileşim, yalnızca belirli değişkenler değiştiğinde tetiklenecek
    localStorage.setItem("selectedQuery", selectedQuery);
    localStorage.setItem("selectedReportTitle", selectedReportTitle);
    localStorage.setItem("selectedRecNo", selectedRecNo);
  }, [selectedQuery, selectedReportTitle, selectedRecNo]);

  return (
    <UseContext.Provider
      value={{
        selectedQuery,
        setSelectedQuery,
        selectedReportTitle,
        setSelectedReportTitle,
        selectedRecNo,
        setSelectedRecNo,
        //selectedInitRun,
        //setSelectedInitRun,
      }}
    >
      {children}
    </UseContext.Provider>
  );
};

export const useQuery = () => useContext(UseContext);

// TODO BURADA YAPMAM GEREKEN SET EDİLİRKEN KONTROL ETMESİ ÖRNEĞİN YENİ Bİ ŞEY SET EDİLCEKSE ESKİSİNE NİN RECnOSUNA baksın var ise set etmesin yok ise altına doğru set etmeli bunu nasıl yapcaz
