import React, { createContext, useContext, useState, useEffect } from "react";

const UseContext = createContext();

export const UseProvider = ({ children }) => {
  const [selectedQuery, setSelectedQuery] = useState(
    localStorage.getItem("selectedQuery")
  );
  const [selectedReportTitle, setSelectedReportTitle] = useState(
    localStorage.getItem("selectedReportTitle")
  );
  const [selectedRecNo, setSelectedRecNo] = useState(
    localStorage.getItem("selectedRecNo")
  );

  // localStorage key'lerini değiştiren fonksiyon
  const changeLocalStorageKeys = () => {
    localStorage.removeItem("selectedQuery");
    localStorage.removeItem("selectedReportTitle");
    localStorage.removeItem("selectedRecNo");

    // Yeni key'lerle localStorage'a yeniden yazma
    localStorage.setItem("newSelectedQuery", selectedQuery);
    localStorage.setItem("newSelectedReportTitle", selectedReportTitle);
    localStorage.setItem("newSelectedRecNo", selectedRecNo);
  };

  return (
    <UseContext.Provider
      value={{
        selectedQuery,
        setSelectedQuery,
        selectedReportTitle,
        setSelectedReportTitle,
        selectedRecNo,
        setSelectedRecNo,
        changeLocalStorageKeys, // Dışarıya açık bir fonksiyon
      }}
    >
      {children}
    </UseContext.Provider>
  );
};

export const useQuery = () => useContext(UseContext);

