import React, { useState, useEffect } from "react";
import axios from "axios";

function ReportButton() {
  const [key, setKey] = useState([]);
  const [value, setValue] = useState([]);

  useEffect(() => {
    fetchData(); // Sayfa yüklendiğinde veriyi çek
  }, []); // Boş bağımlılık dizisi, bu useEffect sadece bir kez çalışır

  const fetchData = async () => {
    try {
      const response = await axios.get("http://localhost:3000/report-page");

      const firstData = response.data[0]; // İlk veri öğesini al

      if (firstData) {
        setKey(Object.keys(firstData)); // İlk verinin key'lerini ayarla

        const valueArray = response.data.map((item) => Object.values(item)); // Tüm veri öğelerini diziye dönüştür

        setValue(valueArray); // Tüm veriyi ayarla
      }
    } catch (error) {
      console.error("Error fetching data", error);
    }
  };

  return (
    <div>
      <button onClick={fetchData}>Veriyi Getir</button>
      {key.length > 0 && value.length > 0 && (
        <table>
          <tbody>
            <tr>
              {key.map((item, index) => (
                <td key={index}>{item}</td>
              ))}
            </tr>
            {value.map((innerArray, outerIndex) => (
              <tr key={outerIndex}>
                {innerArray.map((item, innerIndex) => (
                  <td key={innerIndex}>{item}</td>
                ))}
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default ReportButton;
