import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import { useQuery } from "./useContext";

function TreeView() {
  const [recNo, setRecNo] = useState([]);
  const [reportTitle, setReportTitle] = useState([]);
  const [menuName, setMenuName] = useState([]);
  const [query, setQuery] = useState([]);
  const [initRun, setInitRun] = useState([]);
  const {
    setSelectedReportTitle,
    setSelectedQuery,
    setSelectedRecNo,
    setSelectedInitRun,
  } = useQuery();

  useEffect(() => {
    async function fetchMenuItems() {
      try {
        const response = await axios.get("/tree-view");

        const menuArray = [];
        const queryArray = [];
        const reportTitleArray = [];
        const recNoArray = [];
        const initRunArray = [];
        let iteraty,
          recNo,
          menuAdi,
          raporBaslik,
          selectSorgusu,
          baslangictaRaporCalissinMi;
        const tabloTanimlariDegerleri = Object.values(response.data);

        for (let i = 0; i < tabloTanimlariDegerleri.length; i++) {
          iteraty = response.data[i];
          recNo = Object.values(iteraty)[0];
          menuAdi = Object.values(iteraty)[2];
          raporBaslik = Object.values(iteraty)[5];
          selectSorgusu = Object.values(iteraty)[6];
          baslangictaRaporCalissinMi = Object.values(iteraty)[7];

          menuArray.push(menuAdi);
          queryArray.push(selectSorgusu);
          reportTitleArray.push(raporBaslik);
          recNoArray.push(recNo);
          initRunArray.push(baslangictaRaporCalissinMi);
        }
        setMenuName(menuArray);
        setQuery(queryArray);
        setInitRun(initRunArray);
        setRecNo(recNoArray);
        setReportTitle(reportTitleArray);
      } catch (error) {
        console.error("Failed to fetch menu items: ", error);
      }
    }
    fetchMenuItems();
  }, []);

  const handleClick = async (index) => {
    const selectedReportTitle = reportTitle[index];
    const selectedQuery = query[index];
    const selectedRecNo = recNo[index];
    const selectedInitRun = initRun[index];

    // const localStorageKey = `report_${selectedRecNo}`;

    // localStorage.setItem(`${localStorageKey}_selectedQuery`, selectedQuery);
    // localStorage.setItem(
    //   `${localStorageKey}_selectedReportTitle`,
    //   selectedReportTitle
    // );
    // localStorage.setItem(`${localStorageKey}_selectedRecNo`, selectedRecNo);
    // localStorage.setItem(`${localStorageKey}_selectedInitRun`, selectedInitRun);

    setSelectedReportTitle(selectedReportTitle);
    setSelectedQuery(selectedQuery);
    setSelectedRecNo(selectedRecNo);
    setSelectedInitRun(selectedInitRun);
  };

  return (
    <div className="p-4 border rounded-lg shadow-md">
      <h1 className="text-2xl font-bold mb-4">RAPORLAR</h1>
      <ul>
        {menuName.map((menuItem, index) => (
          <li key={index} className="mb-2">
            <Link
              to={`/report-page/${index + 1}`}
              onClick={() => handleClick(index)}
              className="block px-4 py-2 w-40 bg-blue-500 text-white rounded-md hover:bg-blue-600"
              target="_blank"
            >
              {menuItem}
            </Link>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default TreeView;
