import React, { useState, useEffect } from "react";
import axios from "axios";
import ReportButton from "./buttonReport";

function ButtonCriterion() {
  const [criteriaTitle, setCriteriaTitle] = useState([]);
  const [criteriaType, setCriteriaType] = useState([]);
  const [filterValues, setFilterValues] = useState({}); // Filtre değerlerini saklayacak state

  const [reportData, setReportData] = useState({ key: [], value: [] });

  const fetchData = async () => {
    try {
      const response = await axios.get("http://localhost:3000/criterion");

      const criteriaArray = [];
      for (let i = 0; i < Object.values(response.data).length; i++) {
        criteriaArray.push(Object.values(response.data[i])[3]);
      }
      setCriteriaTitle(criteriaArray);

      const criteriaTypeArray = [];
      for (let i = 0; i < Object.values(response.data).length; i++) {
        criteriaTypeArray.push(Object.values(response.data[i])[4]);
      }
      setCriteriaType(criteriaTypeArray);
    } catch (error) {
      console.error("Error fetching data", error);
    }
  };

  const handleFilterChange = (index, value) => {
    // Filtre değerlerini güncelle
    setFilterValues((prevState) => ({
      ...prevState,
      [criteriaTitle[index]]: value,
    }));
  };

  const handleRaporlaClick = async () => {
    try {
      // Filtrelenmiş verileri almak için rapor verisi alınacak
      const response = await axios.get("http://localhost:3000/report");

      // Filtreleme işlevini gerçekleştir
      const filteredData = response.data.filter((item) => {
        // Her öğe için filtrelere göre kontrol et
        return Object.entries(filterValues).every(([key, value]) => {
          return item[key] === value;
        });
      });

      // Filtrelenmiş verileri ayarla
      setReportData({
        key: Object.keys(filteredData[0]), // Sadece başlıklar
        value: filteredData,
      });
    } catch (error) {
      console.error("Error fetching data", error);
    }
  };

  useEffect(() => {
    fetchData();
  }, []); // Sadece bileşen yüklendiğinde bir kere veriyi almak için

  return (
    <div className="p-4">
      <h1 className="text-2xl font-bold mb-4">Kriterler</h1>
      <button
        className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded mb-4"
        onClick={fetchData}
      >
        Kriterleri tablosunu gör
      </button>

      <div>
        {criteriaTitle.map((title, index) => (
          <div key={index} className="mb-6">
            <h2 className="text-lg font-semibold mb-2">
              Kriter Başlığı: {title}
            </h2>

            {criteriaType[index] === "Numeric" ? (
              <div>
                <input
                  type="text"
                  placeholder="Alt Sayı"
                  className="border rounded-md px-3 py-2 mb-2"
                  onChange={(e) => handleFilterChange(index, e.target.value)}
                />
                <br />
                <input
                  type="text"
                  placeholder="Sayı Üst Sayı"
                  className="border rounded-md px-3 py-2"
                />
              </div>
            ) : criteriaType[index] === "Text" ? (
              <input
                type="text"
                placeholder="Metin giriniz"
                className="border rounded-md px-3 py-2 mb-2"
                onChange={(e) => handleFilterChange(index, e.target.value)}
              />
            ) : criteriaType[index] === "Date" ? (
              <input type="date" className="border rounded-md px-3 py-2 mb-2" />
            ) : criteriaType[index] === "Combo" ? (
              <select className="border rounded-md px-3 py-2 mb-2">
                <option>Seçenek 1</option>
                <option>Seçenek 2</option>
                <option>Seçenek 3</option>
              </select>
            ) : null}
          </div>
        ))}
      </div>

      <button
        className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded"
        onClick={handleRaporlaClick}
      >
        RAPORLA
      </button>

      {/* Rapor butonu ve rapor verisi */}
      <ReportButton reportData={reportData} />
    </div>
  );
}

export default ButtonCriterion;
