import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
//import { useQuery } from "./useContext";

function TreeView() {
  const [menuData, setMenuData] = useState([]);

  //const [query, setQuery] = useState([]);
  // const [recNo, setRecNo] = useState([]);
  // const [reportTitle, setReportTitle] = useState([]);
  // const [menuName, setMenuName] = useState([]);
  //const [initRun, setInitRun] = useState([]);
  const [data, setData] = useState([]);

  // const {
  //   setSelectedReportTitle,
  //   setSelectedQuery,
  //   setSelectedRecNo,
  //   setSelectedInitRun,
  // } = useQuery();

  useEffect(() => {
    async function fetchMenuItems() {
      try {
        const response = await axios.get("/tree-view");

        const menuDataArray = [];

        for (let i = 0; i < response.data.length; i++) {
          const item = response.data[i];
          const menuDataItem = {
            recNo: item[0],
            menuName: item[2],
            reportTitle: item[5],
            query: item[6],
            initRun: item[7],
          };
          menuDataArray.push(menuDataItem);
          console.log(menuDataItem);
        }

        setMenuData(menuDataArray);
      } catch (error) {
        console.error("Failed to fetch menu items: ", error);
      }
    }
    fetchMenuItems();
  }, []);

  return (
    <div className="p-4 border rounded-lg shadow-md">
      <h1 className="text-2xl font-bold mb-4">RAPORLAR</h1>
      <ul>
        {menuData.map((item, index) => (
          <li key={index} className="mb-2">
            <Link
              to={`/report-page/${index + 1}/${item.query}`}
              className="block px-4 py-2 w-40 bg-blue-500 text-white rounded-md hover:bg-blue-600"
              target="_blank"
            >
              {item.menuName}
            </Link>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default TreeView;

