import React, { createContext, useContext, useState, useEffect } from "react";

const UseContext = createContext();

export const UseProvider = ({ children }) => {
  const [selectedQuery, setSelectedQuery] = useState(() => {
    const existingQuery = localStorage.getItem("selectedQuery");
    return existingQuery ? `${existingQuery}\n` : "";
  });
  const [selectedReportTitle, setSelectedReportTitle] = useState(() => {
    const existingTitle = localStorage.getItem("selectedReportTitle");
    return existingTitle ? `${existingTitle}\n` : "";
  });
  const [selectedRecNo, setSelectedRecNo] = useState(() => {
    const existingRecNo = localStorage.getItem("selectedRecNo");
    return existingRecNo ? `${existingRecNo}\n` : "";
  });
  const [selectedInitRun, setSelectedInitRun] = useState(() => {
    const existingInitRun = localStorage.getItem("selectedInitRun");
    return existingInitRun ? `${existingInitRun}\n` : "";
  });

  useEffect(() => {
    localStorage.setItem("selectedQuery", selectedQuery);
    localStorage.setItem("selectedReportTitle", selectedReportTitle);
    localStorage.setItem("selectedRecNo", selectedRecNo);
    localStorage.setItem("selectedInitRun", selectedInitRun);
  }, [selectedQuery, selectedReportTitle, selectedRecNo, selectedInitRun]);

  return (
    <UseContext.Provider
      value={{
        selectedQuery,
        setSelectedQuery,
        selectedReportTitle,
        setSelectedReportTitle,
        selectedRecNo,
        setSelectedRecNo,
        selectedInitRun,
        setSelectedInitRun,
      }}
    >
      {children}
    </UseContext.Provider>
  );
};

export const useQuery = () => useContext(UseContext);
