import React, { useState } from "react";
import axios from "axios";

function ButtonCriterion() {
  const [criteriaTitle, setCriteriaTitle] = useState([]);
  const [criteriaType, setCriteriaType] = useState([]);

  const fetchData = async () => {
    try {
      const response = await axios.get("http://localhost:3000/criterion");

      const criteriaArray = [];
      const criteriaTypeArray = [];

      for (let i = 0; i < response.data.length; i++) {
        criteriaArray.push(response.data[i][3]);
        criteriaTypeArray.push(response.data[i][4]);
      }

      setCriteriaTitle(criteriaArray);
      setCriteriaType(criteriaTypeArray);
    } catch (error) {
      console.error("Error fetching data", error);
    }
  };

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-2xl font-bold mb-4">Kriterler</h1>
      <button
        className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded mb-4"
        onClick={fetchData}
      >
        Kriterleri tablosunu gör
      </button>

      <div>
        {criteriaTitle.map((title, index) => (
          <div key={index} className="mb-4">
            <h2 className="text-lg font-semibold mb-2">
              Kriter Başlığı: {title}
            </h2>

            {criteriaType[index] === "Numeric" ? (
              <div>
                <input
                  type="text"
                  placeholder="Sayı giriniz"
                  className="border border-gray-300 px-3 py-2 rounded"
                />
                <br />
                <input
                  type="text"
                  placeholder="Sayı giriniz"
                  className="mt-2 border border-gray-300 px-3 py-2 rounded"
                />
              </div>
            ) : criteriaType[index] === "Text" ? (
              <input
                type="text"
                placeholder="Metin giriniz"
                className="border border-gray-300 px-3 py-2 rounded"
              />
            ) : criteriaType[index] === "Date" ? (
              <input
                type="date"
                className="border border-gray-300 px-3 py-2 rounded"
              />
            ) : criteriaType[index] === "Combo" ? (
              <select className="border border-gray-300 px-3 py-2 rounded">
                <option>Seçenek 1</option>
                <option>Seçenek 2</option>
                <option>Seçenek 3</option>
              </select>
            ) : null}
          </div>
        ))}
      </div>

      <button className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded">
        RAPORLA
      </button>
    </div>
  );
}

export default ButtonCriterion;
