import React, { useState } from "react";
import axios from "axios";

function ButtonCriterion() {
  const [criteriaTitle, setCriteriaTitle] = useState([]); //TODO Krtierin başlığını al ve gir input alanının yanına
  const [criteriaType, setCriteriaType] = useState([]); //TODO Kriterin Tipine göre gelecek input alanı

  const [filter, setFilter] = useState([]);

  //TODO CRİTERİALTİTLE'LARIN YER ALDIĞI ARRAY'E BAK EĞER ORADA HERHANGİ BİR KEY BULURSAN O FİLTRELENCEK DEMEKTİR

  const fetchData = async () => {
    try {
      const response = await axios.get("http://localhost:3000/criterion");

      const criteriaArray = [];
      for (let i = 0; i < Object.values(response.data).length; i++) {
        criteriaArray.push(Object.values(response.data[i])[3]); //TODOSdece Başlık depolanır
      }
      setCriteriaTitle(criteriaArray);

      const criteriaTypeArray = [];
      for (let i = 0; i < Object.values(response.data).length; i++) {
        criteriaTypeArray.push(Object.values(response.data[i])[4]); //TODO Kriter tipi
      }

      setCriteriaType(criteriaTypeArray);
    } catch (error) {
      console.error("Error fetching data", error);
    }
  };

  return (
    <div className="p-4">
      <h1 className="text-2xl font-bold mb-4">Kriterler</h1>
      <button
        className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded mb-4"
        onClick={fetchData}
      >
        Kriterleri tablosunu gör
      </button>

      <div>
        {criteriaTitle.map((title, index) => (
          <div key={index} className="mb-6">
            <h2 className="text-lg font-semibold mb-2">
              Kriter Başlığı: {title}
            </h2>

            {criteriaType[index] === "Numeric" ? (
              <div>
                <input
                  type="text"
                  placeholder="Alt Sayı"
                  className="border rounded-md px-3 py-2 mb-2"
                />
                <br />
                <input
                  type="text"
                  placeholder="Sayı Üst Sayı"
                  className="border rounded-md px-3 py-2"
                />
              </div>
            ) : criteriaType[index] === "Text" ? (
              <input
                type="text"
                placeholder="Metin giriniz"
                className="border rounded-md px-3 py-2 mb-2"
              />
            ) : criteriaType[index] === "Date" ? (
              <input type="date" className="border rounded-md px-3 py-2 mb-2" />
            ) : criteriaType[index] === "Combo" ? (
              <select className="border rounded-md px-3 py-2 mb-2">
                <option>Seçenek 1</option>
                <option>Seçenek 2</option>
                <option>Seçenek 3</option>
              </select>
            ) : null}
          </div>
        ))}
      </div>

      <button className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded">
        RAPORLA
      </button>
    </div>
  );
}

export default ButtonCriterion;
