import React, { createContext, useContext, useState, useEffect } from "react";

const UseContext = createContext();

export const UseProvider = ({ children }) => {
  const [selectedQuery, setSelectedQuery] = useState(
    localStorage.getItem("selectedQuery") || ""
  );
  const [selectedReportTitle, setSelectedReportTitle] = useState(
    localStorage.getItem("selectedReportTitle") || ""
  );
  const [selectedRecNo, setSelectedRecNo] = useState(
    localStorage.getItem("selectedRecNo") || ""
  );
  const [selectedInitRun, setSelectedInitRun] = useState(
    localStorage.getItem("selectedInitRun") || ""
  );

  useEffect(() => {
    localStorage.setItem("selectedQuery", selectedQuery);
    localStorage.setItem("selectedReportTitle", selectedReportTitle);
    localStorage.setItem("selectedRecNo", selectedRecNo);
    localStorage.setItem("selectedInitRun", selectedInitRun);
  }, [selectedQuery, selectedReportTitle, selectedRecNo, selectedInitRun]);

  return (
    <UseContext.Provider
      value={{
        selectedQuery,
        setSelectedQuery,
        selectedReportTitle,
        setSelectedReportTitle,
        selectedRecNo,
        setSelectedRecNo,
        selectedInitRun,
        setSelectedInitRun,
      }}
    >
      {children}
    </UseContext.Provider>
  );
};

export const useQuery = () => useContext(UseContext);
