import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import { useQuery } from "./useContext";

function TreeView() {
  //RAPOR TANİMNLARİ TABLOSUNUN  KOLONLARININ STATE TANIMLARI
  const [recNo, setRecNo] = useState([]); //TODO REPORT PAGE SAYFASINA AKTARILACAK PARAMETRİK OLARAK//
  const [reportTitle, setReportTitle] = useState([]); // DİREK OLARAK
  const [menuName, setMenuName] = useState([]); // TODO REPORT PAGE SAYFASINA AKTARILACAK SAYFANIN BAŞLIĞI  OLARAK
  const [query, setQuery] = useState([]); // TODO REPORT PAGE SAYFASINA AKATARIALACAK SORGU OLARAK  OLARAK
  const [initRun, setInitRun] = useState([]); // TODO REPORT PAGE SAYFASINA AKATARILACAK DEĞER  OLARAK
  const {
    setSelectedReportTitle,
    setSelectedQuery,
    setSelectedRecNo,
    setSelectedInitRun,
    setSelectedMenuName,
  } = useQuery(); // AKTARILACAK VERİLER USEQUERY SAYESİNDE AKTARILIR

  useEffect(() => {
    async function fetchMenuItems() {
      try {
        const response = await axios.get("/tree-view");

        //BOŞ ARRAYLER İÇLERİNİ RAPOR TANIMLARININ DEĞERLERİ İLE DOLDURULMAK İÇİN OLUŞTURULDU
        const menuArray = [];
        const queryArray = [];
        const reportTitleArray = [];
        const recNoArray = [];
        const initRunArray = [];
        let iteraty,
          recNo,
          menuAdi,
          raporBaslik,
          selectSorgusu,
          baslangictaRaporCalissinMi;
        const tabloTanimlariDegerleri = Object.values(response.data);

        for (let i = 0; i < tabloTanimlariDegerleri.length; i++) {
          //
          iteraty = response.data[i];
          recNo = Object.values(iteraty)[0];
          menuAdi = Object.values(iteraty)[2];
          raporBaslik = Object.values(iteraty)[5];
          selectSorgusu = Object.values(iteraty)[6];
          baslangictaRaporCalissinMi = Object.values(iteraty)[7];

          menuArray.push(menuAdi); //MENU ADI
          queryArray.push(selectSorgusu); //SELECT SORGUSU
          reportTitleArray.push(raporBaslik); //RAPOR BAŞLIK
          recNoArray.push(recNo); // RECNO
          initRunArray.push(baslangictaRaporCalissinMi); // BAŞLANGIÇTA RAPOR ÇALIŞSIN MI
        }
        setMenuName(menuArray); // menuName --- SAYFADA KULLANILACAK
        setQuery(queryArray); // query ---->useContext
        setInitRun(initRunArray); //  initRun ----> useContext
        setRecNo(recNoArray); // recNo   -----> useContext
        setReportTitle(reportTitleArray); // reportTitle ----> useContext
      } catch (error) {
        console.error("Failed to fetch menu items: ", error);
      }
    }
    fetchMenuItems();
  }, []);

  const handleClick = async (index) => {
    const selectedReportTitle = reportTitle[index];
    const selectedQuery = query[index];
    const selectedRecNo = recNo[index];
    const selectedInitRun = initRun[index];
    const selectedMenuName = menuName[index];

    setSelectedMenuName(selectedMenuName);
    setSelectedReportTitle(selectedReportTitle);
    setSelectedQuery(selectedQuery);
    setSelectedRecNo(selectedRecNo);
    setSelectedInitRun(selectedInitRun);
  };
  return (
    <div className="p-4 border rounded-lg shadow-md">
      <h1 className="text-2xl font-bold mb-4">RAPORLAR</h1>
      <ul>
        {menuName.map((menuItem, index) => (
          <li key={index} className="mb-2">
            <Link
              to={{
                pathname: `/report-page/${menuName}`,
              }}
              onClick={() => handleClick(index)}
              className="block px-4 py-2 w-40 bg-blue-500 text-white rounded-md hover:bg-blue-600"
              target="_blank"
            >
              {menuItem}
            </Link>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default TreeView;
