import React, { useState } from "react";
import axios from "axios";

function ButtonCriterion() {
  const [criteriaTitle, setCriteriaTitle] = useState([]);
  const [criteriaType, setCriteriaType] = useState([]);
  const [formData, setFormData] = useState({}); // Kullanıcının girdiği verileri saklamak için state

  const fetchData = async () => {
    try {
      const response = await axios.get("http://localhost:3000/criterion");

      const criteriaArray = [];
      const criteriaTypeArray = [];
      for (let i = 0; i < Object.values(response.data).length; i++) {
        criteriaArray.push(Object.values(response.data[i])[3]);
        criteriaTypeArray.push(Object.values(response.data[i])[4]);
      }
      setCriteriaTitle(criteriaArray);
      setCriteriaType(criteriaTypeArray);
    } catch (error) {
      console.error("Error fetching data", error);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // FormData'ya göre gerekli işlemleri gerçekleştirin
      await axios.post("http://localhost:3000/filtredReport", formData);
      console.log("Form submitted successfully!");
    } catch (error) {
      console.error("Error submitting form", error);
    }
  };

  const handleInputChange = (e, title) => {
    const value = e.target.value;
    setFormData({ ...formData, [title]: value }); // FormData güncelleniyor
  };

  return (
    <div className="p-4">
      <h1 className="text-2xl font-bold mb-4">Kriterler</h1>
      <button
        className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded mb-4"
        onClick={fetchData}
      >
        Kriterleri tablosunu gör
      </button>

      <form onSubmit={handleSubmit}>
        {criteriaTitle.map((title, index) => (
          <div key={index} className="mb-6">
            <h2 className="text-lg font-semibold mb-2">
              Kriter Başlığı: {title}
            </h2>

            {criteriaType[index] === "Numeric" ? (
              <div>
                <input
                  type="text"
                  placeholder="Alt Sayı"
                  className="border rounded-md px-3 py-2 mb-2"
                  onChange={(e) => handleInputChange(e, title)}
                />
                <br />
                <input
                  type="text"
                  placeholder="Üst Sayı"
                  className="border rounded-md px-3 py-2"
                  onChange={(e) => handleInputChange(e, title)}
                />
              </div>
            ) : criteriaType[index] === "Text" ? (
              <input
                type="text"
                placeholder="Metin giriniz"
                className="border rounded-md px-3 py-2 mb-2"
                onChange={(e) => handleInputChange(e, title)}
              />
            ) : criteriaType[index] === "Date" ? (
              <input
                type="date"
                className="border rounded-md px-3 py-2 mb-2"
                onChange={(e) => handleInputChange(e, title)}
              />
            ) : criteriaType[index] === "Combo" ? (
              <select
                className="border rounded-md px-3 py-2 mb-2"
                onChange={(e) => handleInputChange(e, title)}
              >
                <option>Seçenek 1</option>
                <option>Seçenek 2</option>
                <option>Seçenek 3</option>
              </select>
            ) : null}
          </div>
        ))}
        <button
          type="submit"
          className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded"
        >
          RAPORLA
        </button>
      </form>
    </div>
  );
}

export default ButtonCriterion;
